import React, { useState, useEffect } from 'react';
import axios from 'axios';

const sampleMenu = [ { id: 1, name: "Sambar idly", price: 40 }, 
  { id: 2, name: "mysore Bonda", price: 50 }, 
  { id: 3, name: "onion Dosa", price: 60 }, 
  { id: 4, name: " wada ", price: 60 }
 ];

const CustomerView = () => {
  const [menu] = useState(sampleMenu);
  const [cart, setCart] = useState([]);
  const [table, setTable] = useState(new URLSearchParams(window.location.search).get("table") || "");
  const [orderId, setOrderId] = useState(null);
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("");

  const addToCart = (item) => {
    setCart((prev) => {
      const exists = prev.find((i) => i.id === item.id);
      return exists
        ? prev.map((i) => (i.id === item.id ? { ...i, qty: i.qty + 1 } : i))
        : [...prev, { ...item, qty: 1 }];
    });
  };

  const removeFromCart = (item) => {
    setCart((prev) => {
      return prev
        .map((i) => (i.id === item.id ? { ...i, qty: i.qty - 1 } : i))
        .filter((i) => i.qty > 0); // remove if qty becomes 0
    });
  };

  const submitOrder = async () => {
    if (!table || cart.length === 0) {
      alert("Please enter table number and select items.");
      return;
    }

    const order = {
      id: Date.now().toString(),
      table,
      items: cart,
      status: "pending"
    };

    try {
      await axios.post("http://localhost:4000/orders", order);
      setMessage("Order placed! Waiting for admin confirmation...");
      setOrderId(order.id);
      setCart([]);
    } catch (error) {
      console.error(error);
      setMessage("Failed to place order.");
    }
  };

  useEffect(() => {
    if (!orderId) return;

    const interval = setInterval(async () => {
      try {
        const res = await axios.get(`http://localhost:4000/orders/${orderId}`);
        if (res.data.status !== "pending") {
          setStatus(res.data.status);
          clearInterval(interval);
        }
      } catch (err) {
        console.error("Error fetching order status", err);
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [orderId]);

  const total = cart.reduce((sum, item) => sum + item.qty * item.price, 0);

  return (
    <div style={{ padding: 20, fontFamily: "sans-serif", maxWidth: 600, margin: "0 auto" }}>
      <h2>üçΩÔ∏è Welcome to TLJ Tiffen Center</h2>

      <div style={{ marginBottom: 20 }}>
        <label>Table Number: </label>
        <input
          type="text"
          value={table}
          onChange={(e) => setTable(e.target.value)}
          style={{ padding: 6, marginLeft: 10 }}
        />
      </div>

      <h3>üìã Menu</h3>
      <ul style={{ listStyle: "none", paddingLeft: 0 }}>
        {menu.map((item) => (
          <li
            key={item.id}
            style={{
              marginBottom: 10,
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              borderBottom: "1px solid #eee",
              paddingBottom: 5
            }}
          >
            <span>{item.name} - ‚Çπ{item.price}</span>
            <button
              onClick={() => addToCart(item)}
              style={{
                backgroundColor: "#007bff",
                color: "#fff",
                border: "none",
                padding: "6px 12px",
                borderRadius: 4,
                cursor: "pointer"
              }}
            >
              Add
            </button>
          </li>
        ))}
      </ul>

      <h3>üõí Cart</h3>
      {cart.length === 0 ? (
        <p>No items selected.</p>
      ) : (
        <ul style={{ listStyle: "none", paddingLeft: 0 }}>
          {cart.map((item) => (
            <li
              key={item.id}
              style={{
                marginBottom: 10,
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between"
              }}
            >
              <span style={{ flex: 1 }}>
                {item.name} x {item.qty} = ‚Çπ{item.qty * item.price}
              </span>
              <div>
                <button
                  onClick={() => removeFromCart(item)}
                  style={{
                    backgroundColor: "#dc3545",
                    color: "#fff",
                    border: "none",
                    padding: "4px 10px",
                    marginRight: 5,
                    borderRadius: 4,
                    cursor: "pointer"
                  }}
                >
                  ‚ûñ
                </button>
                <button
                  onClick={() => addToCart(item)}
                  style={{
                    backgroundColor: "#28a745",
                    color: "#fff",
                    border: "none",
                    padding: "4px 10px",
                    borderRadius: 4,
                    cursor: "pointer"
                  }}
                >
                  ‚ûï
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}

      {cart.length > 0 && (
        <p style={{ fontWeight: "bold", marginTop: 10 }}>üßæ Total: ‚Çπ{total}</p>
      )}

      <button
        onClick={submitOrder}
        style={{
          marginTop: 20,
          backgroundColor: "#17a2b8",
          color: "#fff",
          border: "none",
          padding: "10px 20px",
          borderRadius: 6,
          cursor: "pointer"
        }}
      >
        Submit Order
      </button>

      {message && <p style={{ color: "green", marginTop: 10 }}>{message}</p>}
      {status && (
        <p
          style={{
            color: status === "accepted" ? "green" : "red",
            marginTop: 10,
            fontWeight: "bold"
          }}
        >
          {status === "accepted"
            ? "‚úÖ Your order was accepted!"
            : "‚ùå Your order was rejected."}
        </p>
      )}
    </div>
  );
};

export default CustomerView;
