import React, { useState, useEffect } from 'react';
import axios from 'axios';

const sampleMenu = [
  { id: 1, name: "Margherita Pizza", price: 250 },
  { id: 2, name: "Paneer Burger", price: 180 },
  { id: 3, name: "Masala Dosa", price: 120 },
  { id: 4, name: "Cold Coffee", price: 90 }
];

const CustomerView = () => {
  const [menu] = useState(sampleMenu);
  const [cart, setCart] = useState([]);
  const [table, setTable] = useState(new URLSearchParams(window.location.search).get("table") || "");
  const [orderId, setOrderId] = useState(null);
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("");

  const addToCart = (item) => {
    setCart((prev) => {
      const exists = prev.find((i) => i.id === item.id);
      return exists
        ? prev.map((i) => (i.id === item.id ? { ...i, qty: i.qty + 1 } : i))
        : [...prev, { ...item, qty: 1 }];
    });
  };

 const submitOrder = async () => {
  if (!table || cart.length === 0) {
    alert("Please enter table number and select items.");
    return;
  }

  const order = {
    id: Date.now().toString(), // ‚úÖ Fix: Ensure ID is always a string
    table,
    items: cart,
    status: "pending"
  };

  try {
    await axios.post("http://localhost:4000/orders", order);
    setMessage("Order placed! Waiting for admin confirmation...");
    setOrderId(order.id);
    setCart([]);
  } catch (error) {
    console.error(error);
    setMessage("Failed to place order.");
  }
};

  // Polling to check order status
  useEffect(() => {
    if (!orderId) return;

    const interval = setInterval(async () => {
      try {
        const res = await axios.get(`http://localhost:4000/orders/${orderId}`);
        if (res.data.status !== "pending") {
          setStatus(res.data.status);
          clearInterval(interval);
        }
      } catch (err) {
        console.error("Error fetching order status", err);
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [orderId]);

  return (
    <div style={{ padding: 20 }}>
      <h2>üçΩÔ∏è Restaurant Menu</h2>

      <div style={{ marginBottom: 10 }}>
        <label>Table Number: </label>
        <input
          type="text"
          value={table}
          onChange={(e) => setTable(e.target.value)}
        />
      </div>

      <h3>üìã Menu</h3>
      <ul>
        {menu.map((item) => (
          <li key={item.id}>
            {item.name} - ‚Çπ{item.price}
            <button onClick={() => addToCart(item)} style={{ marginLeft: 10 }}>
              Add
            </button>
          </li>
        ))}
      </ul>

      <h3>üõí Cart</h3>
      {cart.length === 0 ? (
        <p>No items selected.</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              {item.name} x {item.qty} = ‚Çπ{item.qty * item.price}
            </li>
          ))}
        </ul>
      )}

      <button onClick={submitOrder} style={{ marginTop: 20 }}>
        Submit Order
      </button>

      {message && <p style={{ color: "green" }}>{message}</p>}
      {status && (
        <p style={{ color: status === "accepted" ? "green" : "red" }}>
          {status === "accepted"
            ? "‚úÖ Your order was accepted!"
            : "‚ùå Your order was rejected."}
        </p>
      )}
    </div>
  );
};

export default CustomerView;
