import React, { useState, useEffect } from 'react';
import axios from 'axios';

const sampleMenu = [
  { id: 1, name: "Margherita Pizza", price: 250 },
  { id: 2, name: "Paneer Burger", price: 180 },
  { id: 3, name: "Masala Dosa", price: 120 },
  { id: 4, name: "Cold Coffee", price: 90 }
];

const CustomerView = () => {
  const [menu] = useState(sampleMenu);
  const [cart, setCart] = useState([]);
  const [table, setTable] = useState(new URLSearchParams(window.location.search).get("table") || "");
  const [orderId, setOrderId] = useState(null);
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("");

  const addToCart = (item) => {
    setCart((prev) => {
      const exists = prev.find((i) => i.id === item.id);
      return exists
        ? prev.map((i) => (i.id === item.id ? { ...i, qty: i.qty + 1 } : i))
        : [...prev, { ...item, qty: 1 }];
    });
  };

  const submitOrder = async () => {
    if (!table || cart.length === 0) {
      alert("Please enter table number and select items.");
      return;
    }

    const order = {
      id: Date.now().toString(),
      table,
      items: cart,
      status: "pending"
    };

    try {
      await axios.post("http://localhost:4000/orders", order);
      setMessage("Order placed! Waiting for admin confirmation...");
      setOrderId(order.id);
      setCart([]);
    } catch (error) {
      console.error(error);
      setMessage("Failed to place order.");
    }
  };

  useEffect(() => {
    if (!orderId) return;

    const interval = setInterval(async () => {
      try {
        const res = await axios.get(`http://localhost:4000/orders/${orderId}`);
        if (res.data.status !== "pending") {
          setStatus(res.data.status);
          clearInterval(interval);
        }
      } catch (err) {
        console.error("Error fetching order status", err);
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [orderId]);

  return (
    <div style={{ padding: 30, fontFamily: 'sans-serif', maxWidth: 800, margin: 'auto' }}>
      <h2 style={{ textAlign: 'center' }}>üçΩÔ∏è Restaurant Menu</h2>

      <div style={{ marginBottom: 20 }}>
        <label style={{ fontWeight: 600 }}>Table Number: </label>
        <input
          type="text"
          value={table}
          onChange={(e) => setTable(e.target.value)}
          placeholder="Enter table number"
          style={{
            padding: 8,
            marginLeft: 10,
            borderRadius: 4,
            border: '1px solid #ccc',
            width: 150
          }}
        />
      </div>

      <h3>üìã Menu</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 20 }}>
        {menu.map((item) => (
          <div
            key={item.id}
            style={{
              border: '1px solid #ddd',
              borderRadius: 8,
              padding: 15,
              width: 180,
              boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
            }}
          >
            <h4>{item.name}</h4>
            <p>‚Çπ{item.price}</p>
            <button
              onClick={() => addToCart(item)}
              style={{
                backgroundColor: '#28a745',
                color: '#fff',
                border: 'none',
                padding: '6px 12px',
                borderRadius: 4,
                cursor: 'pointer'
              }}
            >
              ‚ûï Add to Cart
            </button>
          </div>
        ))}
      </div>

      <h3 style={{ marginTop: 30 }}>üõí Your Cart</h3>
      {cart.length === 0 ? (
        <p style={{ fontStyle: 'italic' }}>No items selected.</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id} style={{ marginBottom: 5 }}>
              {item.name} x {item.qty} = ‚Çπ{item.qty * item.price}
            </li>
          ))}
        </ul>
      )}

      <button
        onClick={submitOrder}
        style={{
          marginTop: 20,
          backgroundColor: '#007bff',
          color: '#fff',
          padding: '10px 20px',
          border: 'none',
          borderRadius: 5,
          cursor: 'pointer',
          fontSize: 16
        }}
      >
        üöÄ Submit Order
      </button>

      {message && (
        <p style={{ marginTop: 15, color: '#444', fontWeight: 500 }}>{message}</p>
      )}
      {status && (
        <p
          style={{
            color: status === "accepted" ? "green" : "red",
            fontWeight: "bold",
            marginTop: 10
          }}
        >
          {status === "accepted"
            ? "‚úÖ Your order was accepted!"
            : "‚ùå Your order was rejected."}
        </p>
      )}
    </div>
  );
};

export default CustomerView;
