import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminView = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();

    const interval = setInterval(fetchOrders, 5000);
    return () => clearInterval(interval);
  }, []);

  const fetchOrders = async () => {
    try {
      const res = await axios.get("http://localhost:4000/orders");
      console.log("Fetched orders:", res.data); // ‚úÖ Debug log
      setOrders(res.data);
    } catch (err) {
      console.error("Error fetching orders", err);
    }
  };

  const updateOrderStatus = async (id, newStatus) => {
    try {
      await axios.patch(`http://localhost:4000/orders/${id}`, {
        status: newStatus,
      });
      alert(`Order ${id} has been ${newStatus}`);
      fetchOrders();
    } catch (err) {
      console.error("Error updating order", err);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üßæ Admin Dashboard ‚Äì Incoming Orders</h2>

      {orders.length === 0 && <p>‚è≥ Loading or no orders found...</p>}

      {orders
        .filter((order) => {
          const match = order.status?.toLowerCase() === "pending";
          console.log("Filter check for order", order.id, "‚Üí", match); // ‚úÖ Debug log
          return match;
        })
        .map((order) => (
          <div
            key={order.id}
            style={{
              border: "1px solid #ccc",
              padding: 15,
              marginBottom: 15,
              borderRadius: 5,
            }}
          >
            <h3>Table #{order.table}</h3>
            <ul>
              {order.items.map((item, idx) => (
                <li key={idx}>
                  {item.name} x {item.qty}
                </li>
              ))}
            </ul>
            <div style={{ marginTop: 10 }}>
              <button
                onClick={() => updateOrderStatus(order.id, "accepted")}
                style={{ marginRight: 10 }}
              >
                ‚úÖ Accept
              </button>
              <button
                onClick={() => updateOrderStatus(order.id, "rejected")}
                style={{ backgroundColor: "crimson", color: "#fff" }}
              >
                ‚ùå Reject
              </button>
            </div>
          </div>
        ))}
    </div>
  );
};

export default AdminView;
