import React, { useState } from 'react';
import axios from 'axios';
import { useOrder } from '../context/OrderContext';

const sampleMenu = [
  { id: 1, name: "Margherita Pizza", price: 250 },
  { id: 2, name: "Paneer Burger", price: 180 },
  { id: 3, name: "Masala Dosa", price: 120 },
  { id: 4, name: "Cold Coffee", price: 90 }
];

const CustomerView = () => {
  const [menu] = useState(sampleMenu);
  const [message, setMessage] = useState("");

  const { cart, table, setTable, addToCart, resetOrder } = useOrder();

  const submitOrder = async () => {
    if (!table) {
      alert("Please enter your table number.");
      return;
    }
    if (cart.length === 0) {
      alert("Cart is empty.");
      return;
    }

    const order = {
      id: Date.now(), // generate simple unique ID
      table,
      items: cart,
      status: "pending"
    };

    try {
      // You can use an API call instead
      const stored = JSON.parse(localStorage.getItem("orders") || "[]");
      stored.push(order);
      localStorage.setItem("orders", JSON.stringify(stored));

      setMessage("‚úÖ Order placed successfully!");
      resetOrder();
    } catch (error) {
      console.error(error);
      setMessage("‚ùå Failed to place order.");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üçΩÔ∏è Welcome to the Restaurant Menu</h2>

      <div style={{ marginBottom: 10 }}>
        <label>Table Number: </label>
        <input
          type="text"
          value={table}
          onChange={(e) => setTable(e.target.value)}
        />
      </div>

      <h3>üìã Menu</h3>
      <ul>
        {menu.map((item) => (
          <li key={item.id} style={{ marginBottom: 5 }}>
            {item.name} - ‚Çπ{item.price}
            <button onClick={() => addToCart(item)} style={{ marginLeft: 10 }}>
              Add
            </button>
          </li>
        ))}
      </ul>

      <h3>üõí Cart</h3>
      {cart.length === 0 ? (
        <p>No items selected.</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              {item.name} x {item.qty} = ‚Çπ{item.qty * item.price}
            </li>
          ))}
        </ul>
      )}

      <button onClick={submitOrder} style={{ marginTop: 20 }}>
        Submit Order
      </button>

      {message && <p style={{ color: "green", marginTop: 10 }}>{message}</p>}
    </div>
  );
};

export default CustomerView;
